import logo from './logo.svg';
import './App.css';

import { Component } from 'react';
import CardList from './components/card-list/card-list.component';
import SearchBox from './components/search-box/search-box.component';

class App extends Component {
  constructor(){
    super();
    this.state = {
      monsters: [],
      searchString: ''
    }
  }
  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then((users) => this.setState({monsters: users}));

  }
  onSearchChange = (event) =>{
    const searchString = event.target.value.toLocaleLowerCase();
    this.setState(()=> {return { searchString };});
  }

  render(){
    const { monsters, searchString } = this.state;
    const { onSearchChange } = this;
    const filteredMonsters = monsters.filter(monster => monster.name.toLocaleLowerCase().includes(searchString));
    return (
      <div className="App">
        <h1 className='app-title'>Monsters Rolodex de los jojos</h1>
        <SearchBox onChangeHandler={onSearchChange} placeholder='jiji monstruos' className='monsters-search-box'/> 
        <CardList monsters={filteredMonsters}/>
      </div>
    );
  }

}

export default App;
