import logo from './logo.svg';
import './App.css';

import { Component } from 'react';

class App extends Component {
  constructor(){
    super();
    this.state = {
      name: 'Gilead'
    }
  }
  render(){
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            HI {this.state.name}
          </p>
          <button onClick={() => { 
            this.setState(
            () =>{
              return {name: 'Raul'}
            });
          }}

          >
          Change Name</button>
        </header>
      </div>
    );
  }

}

export default App;
